async function animateSequentially(e,t,i){for(let n=0;n<e.length;n++)await new Promise(s=>{setTimeout(()=>{e[n].classList.add(t),s()},n*i)})}const navListItems=document.querySelectorAll(".js-nav-li"),preintroItems=document.querySelectorAll(".js-preintro-items"),aboutMeItems=document.querySelectorAll(".js-aboutme-animation"),skillListItems=document.querySelectorAll(".js-skills-items-animation"),sideInfo=document.querySelectorAll(".js-sideinfo-animation"),animations=[{items:navListItems,animationClass:"slide-in-blurred-top",delay:100},{items:preintroItems,animationClass:"puff-in-center",delay:100},{items:aboutMeItems,animationClass:"puff-in-center",delay:100},{items:skillListItems,animationClass:"swing-in-top-fwd",delay:100},{items:sideInfo,animationClass:"fade-in",delay:100}],runAnimations=async()=>{for(const e of animations)await animateSequentially(e.items,e.animationClass,e.delay)};runAnimations();