import{projects}from"../data/projects.js";let projectsHTML="";projects.forEach((n,t)=>{projectsHTML+=t%2==0?`<li id="${n.id}" class="js-project-items-animation-left">\n        <h3 class="project-title">${n.title}</h3>\n        <div class="project-content">\n            <div class="project-screenshot">\n                <a\n                    href="${n.githubRepo}"\n                    target="_blank"\n                    ><img\n                    src="${n.screenshot}"\n                    alt="Screenshot of ${n.title}"\n                />\n                </a>\n            </div>\n            <div class="project-description">\n                ${n.description}\n                <div class="github-repo">\n                    <a\n                    href="${n.githubRepo}"\n                    ><span\n                        >${n.githubRepo}</span\n                    ></a\n                    >\n                </div>\n            </div>\n        </div>\n    </li>`:`<li id="${n.id}" class="js-project-items-animation-right">\n        <h3 class="project-title">${n.title}</h3>\n        <div class="project-content">\n            <div class="project-description">\n            ${n.description}\n                <div class="github-repo">\n                    <a\n                    href="${n.githubRepo}"\n                    ><span\n                        >${n.githubRepo}</span\n                    ></a\n                    >\n                </div>\n            </div>\n            <div class="project-screenshot">\n                <a\n                    href="${n.githubRepo}"\n                    target="_blank"\n                    ><img\n                    src="${n.screenshot}"\n                    alt="Screenshot of ${n.title}"\n                /></a>\n            </div>\n        </div>\n    </li>`}),document.querySelector(".projects").innerHTML=projectsHTML;